import type { Express } from "express";
import { createServer, type Server } from "http";
import { createClient } from '@supabase/supabase-js';
import multer from 'multer';
import { uploadContentSchema } from "@shared/schema";
import { z } from "zod";

// Configure multer for file uploads (200MB limit)
const upload = multer({
  storage: multer.memoryStorage(),
  limits: {
    fileSize: 200 * 1024 * 1024, // 200MB
  },
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Initialize Supabase client with service role key (server-side only)
  const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
  const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

  if (!supabaseUrl || !supabaseServiceKey) {
    console.warn("‚ö†Ô∏è  Supabase environment variables not configured");
  }

  let supabase: any = null;
  if (supabaseUrl && supabaseServiceKey) {
    supabase = createClient(supabaseUrl, supabaseServiceKey);

    // Bootstrap: Create buckets if they don't exist
    try {
      const buckets = ['models', 'images', 'videos'];
      for (const bucketName of buckets) {
        const { data: bucket } = await supabase.storage.getBucket(bucketName);
        if (!bucket) {
          await supabase.storage.createBucket(bucketName, {
            public: true,
          });
          console.log(`‚úÖ Created bucket: ${bucketName}`);
        }
      }
    } catch (error) {
      console.error("Error setting up buckets:", error);
    }
  }

  // Health check endpoint
  app.get('/api/health', (req, res) => {
    const envStatus = {
      supabaseUrl: !!process.env.NEXT_PUBLIC_SUPABASE_URL,
      supabaseAnonKey: !!process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY,
      supabaseServiceKey: !!process.env.SUPABASE_SERVICE_ROLE_KEY,
      uploadPassword: !!process.env.UPLOAD_PASSWORD,
    };

    res.json({
      status: 'ok',
      environment: envStatus,
      ready: Object.values(envStatus).every(Boolean),
    });
  });

  // Get content from Supabase Storage buckets
  app.get('/api/content/:bucket', async (req, res) => {
    if (!supabase) {
      return res.status(503).json({ 
        message: 'Supabase not configured. Please set environment variables.' 
      });
    }

    try {
      const { bucket } = req.params;
      const { data: files, error } = await supabase.storage
        .from(bucket)
        .list('', {
          limit: 100,
          offset: 0,
          sortBy: { column: 'created_at', order: 'desc' }
        });

      if (error) {
        return res.status(500).json({ message: error.message });
      }

      // Get public URLs and metadata for each file
      const filesWithUrls = files.map((file: any) => ({
        ...file,
        url: supabase.storage.from(bucket).getPublicUrl(file.name).data.publicUrl,
        metadata: {
          ...file.metadata,
          title: file.metadata?.title,
          description: file.metadata?.description
        }
      }));

      res.json(filesWithUrls);
    } catch (error) {
      res.status(500).json({ message: 'Failed to fetch content' });
    }
  });

  // Upload endpoint with password protection
  app.post('/api/upload', upload.single('file'), async (req, res) => {
    if (!supabase) {
      return res.status(503).json({ 
        message: 'Supabase ch∆∞a ƒë∆∞·ª£c c·∫•u h√¨nh. Vui l√≤ng ki·ªÉm tra bi·∫øn m√¥i tr∆∞·ªùng.' 
      });
    }

    try {
      const { kind, title, password } = req.body;
      const file = req.file;

      // Validate password
      if (password !== process.env.UPLOAD_PASSWORD) {
        return res.status(401).json({ message: 'Sai m·∫≠t kh·∫©u.' });
      }

      if (!file) {
        return res.status(400).json({ message: 'Kh√¥ng c√≥ file ƒë∆∞·ª£c t·∫£i l√™n.' });
      }

      // Validate file type based on kind
      const allowedTypes: Record<string, string[]> = {
        image: ['image/png', 'image/jpeg', 'image/jpg'],
        video: ['video/mp4', 'video/webm'],
        model: ['model/gltf+json', 'model/gltf-binary', 'application/octet-stream'], // GLB files come as octet-stream
      };

      // For models, also check file extension since MIME type might not be accurate
      if (kind === 'model') {
        const isValidModel = allowedTypes.model.includes(file.mimetype) || 
                           file.originalname.toLowerCase().endsWith('.glb') || 
                           file.originalname.toLowerCase().endsWith('.gltf');
        if (!isValidModel) {
          return res.status(400).json({ message: 'Lo·∫°i file model kh√¥ng h·ª£p l·ªá. Ch·ªâ h·ªó tr·ª£ GLB v√† GLTF.' });
        }
      } else if (!allowedTypes[kind]?.includes(file.mimetype)) {
        return res.status(400).json({ message: 'Lo·∫°i file kh√¥ng h·ª£p l·ªá.' });
      }

      // Determine bucket
      const bucketMap: Record<string, string> = {
        image: 'images',
        video: 'videos', 
        model: 'models',
      };
      const bucket = bucketMap[kind];

      // Generate unique filename
      const timestamp = Date.now();
      const extension = file.originalname.split('.').pop();
      const filename = `${timestamp}-${Math.random().toString(36).substr(2, 9)}.${extension}`;

      // Upload to Supabase Storage
      const { data, error } = await supabase.storage
        .from(bucket)
        .upload(filename, file.buffer, {
          contentType: file.mimetype,
          metadata: {
            title: title || file.originalname,
          },
        });

      if (error) {
        return res.status(500).json({ message: `L·ªói upload: ${error.message}` });
      }

      // Get public URL
      const { data: urlData } = supabase.storage
        .from(bucket)
        .getPublicUrl(filename);

      res.json({
        ok: true,
        kind,
        url: urlData.publicUrl,
        path: data.path,
        message: 'Upload th√†nh c√¥ng!',
      });

    } catch (error) {
      console.error('Upload error:', error);
      res.status(500).json({ message: 'L·ªói server khi upload file.' });
    }
  });

  // Update file metadata endpoint
  app.patch('/api/content/:bucket/:filename', async (req, res) => {
    if (!supabase) {
      return res.status(503).json({ 
        message: 'Supabase ch∆∞a ƒë∆∞·ª£c c·∫•u h√¨nh. Vui l√≤ng ki·ªÉm tra bi·∫øn m√¥i tr∆∞·ªùng.' 
      });
    }

    try {
      const { bucket, filename } = req.params;
      const { title, description, password } = req.body;

      // Validate password
      if (password !== process.env.UPLOAD_PASSWORD) {
        return res.status(401).json({ message: 'Sai m·∫≠t kh·∫©u.' });
      }

      // Get the existing file first
      const { data: fileData, error: downloadError } = await supabase.storage
        .from(bucket)
        .download(filename);

      if (downloadError) {
        return res.status(500).json({ message: `L·ªói t·∫£i file: ${downloadError.message}` });
      }

      // Re-upload with updated metadata
      const { data, error } = await supabase.storage
        .from(bucket)
        .upload(filename, fileData, {
          upsert: true,
          metadata: {
            title: title || filename,
            description: description || '',
          },
        });

      if (error) {
        return res.status(500).json({ message: `L·ªói c·∫≠p nh·∫≠t: ${error.message}` });
      }

      res.json({
        ok: true,
        message: 'C·∫≠p nh·∫≠t th√†nh c√¥ng!',
      });

    } catch (error) {
      console.error('Update error:', error);
      res.status(500).json({ message: 'L·ªói server khi c·∫≠p nh·∫≠t file.' });
    }
  });

  // Delete file endpoint
  app.delete('/api/content/:bucket/:filename', async (req, res) => {
    if (!supabase) {
      return res.status(503).json({ 
        message: 'Supabase ch∆∞a ƒë∆∞·ª£c c·∫•u h√¨nh. Vui l√≤ng ki·ªÉm tra bi·∫øn m√¥i tr∆∞·ªùng.' 
      });
    }

    try {
      const { bucket, filename } = req.params;
      const { password } = req.body;

      // Validate password
      if (password !== process.env.UPLOAD_PASSWORD) {
        return res.status(401).json({ message: 'Sai m·∫≠t kh·∫©u.' });
      }

      // Delete file from storage
      const { error } = await supabase.storage
        .from(bucket)
        .remove([filename]);

      if (error) {
        return res.status(500).json({ message: `L·ªói x√≥a file: ${error.message}` });
      }

      res.json({
        ok: true,
        message: 'X√≥a file th√†nh c√¥ng!',
      });

    } catch (error) {
      console.error('Delete error:', error);
      res.status(500).json({ message: 'L·ªói server khi x√≥a file.' });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

