@import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@300;400;500;600;700;800;900&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 4%);
  --foreground: hsl(0, 0%, 98%);
  --card: hsl(0, 0%, 6%);
  --card-foreground: hsl(0, 0%, 98%);
  --popover: hsl(0, 0%, 6%);
  --popover-foreground: hsl(0, 0%, 98%);
  --primary: hsl(217, 91%, 60%);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsl(217, 32%, 17%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --muted: hsl(217, 32%, 17%);
  --muted-foreground: hsl(215, 20%, 65%);
  --accent: hsl(38, 92%, 50%);
  --accent-foreground: hsl(0, 0%, 9%);
  --destructive: hsl(0, 62%, 30%);
  --destructive-foreground: hsl(0, 85%, 97%);
  --border: hsl(217, 32%, 17%);
  --input: hsl(217, 32%, 17%);
  --ring: hsl(217, 91%, 60%);
  --radius: 0.75rem;

  --font-sans: 'Montserrat', sans-serif;
  --font-serif: 'Montserrat', sans-serif;
  --font-mono: 'Montserrat', monospace;
  --font-display: 'Montserrat', sans-serif;

  --shadow-2xs: 0px 2px 0px 0px hsl(217, 91%, 60% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(217, 91%, 60% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(217, 91%, 60% / 0.00), 0px 1px 2px -1px hsl(217, 91%, 60% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(217, 91%, 60% / 0.00), 0px 1px 2px -1px hsl(217, 91%, 60% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(217, 91%, 60% / 0.00), 0px 2px 4px -1px hsl(217, 91%, 60% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(217, 91%, 60% / 0.00), 0px 4px 6px -1px hsl(217, 91%, 60% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(217, 91%, 60% / 0.00), 0px 8px 10px -1px hsl(217, 91%, 60% / 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(217, 91%, 60% / 0.00);
  --tracking-normal: 0em;
  --spacing: 0.25rem;
}

.dark {
  --background: hsl(0, 0%, 4%);
  --foreground: hsl(0, 0%, 98%);
  --card: hsl(0, 0%, 6%);
  --card-foreground: hsl(0, 0%, 98%);
  --popover: hsl(0, 0%, 6%);
  --popover-foreground: hsl(0, 0%, 98%);
  --primary: hsl(217, 91%, 60%);
  --primary-foreground: hsl(0, 0%, 98%);
  --secondary: hsl(217, 32%, 17%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --muted: hsl(217, 32%, 17%);
  --muted-foreground: hsl(215, 20%, 65%);
  --accent: hsl(38, 92%, 50%);
  --accent-foreground: hsl(0, 0%, 9%);
  --destructive: hsl(0, 62%, 30%);
  --destructive-foreground: hsl(0, 85%, 97%);
  --border: hsl(217, 32%, 17%);
  --input: hsl(217, 32%, 17%);
  --ring: hsl(217, 91%, 60%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Montserrat', sans-serif;
  }

  .font-display {
    font-family: var(--font-display);
  }
}

/* Custom styles for the Vietnamese game website */
.hero-gradient {
  background: linear-gradient(135deg, rgba(30, 64, 175, 0.3) 0%, rgba(245, 158, 11, 0.2) 100%);
}

/* Golden text style matching the reference image */
.golden-text {
  background: linear-gradient(45deg, #FFD700, #FFA500, #FFFF99, #DAA520, #B8860B);
  background-size: 200% 200%;
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  text-shadow: 0 0 30px rgba(255, 215, 0, 0.5), 0 0 60px rgba(255, 215, 0, 0.3);
  animation: golden-glow 3s ease-in-out infinite alternate;
}

@keyframes golden-glow {
  0% {
    background-position: 0% 50%;
    text-shadow: 0 0 20px rgba(255, 215, 0, 0.4), 0 0 40px rgba(255, 215, 0, 0.2);
  }
  100% {
    background-position: 100% 50%;
    text-shadow: 0 0 30px rgba(255, 215, 0, 0.6), 0 0 60px rgba(255, 215, 0, 0.4);
  }
}

.masonry {
  column-count: 3;
  column-gap: 1rem;
  column-fill: balance;
}

@media (max-width: 768px) {
  .masonry {
    column-count: 2;
  }
}

@media (max-width: 480px) {
  .masonry {
    column-count: 1;
  }
}

.masonry-item {
  break-inside: avoid;
  margin-bottom: 1rem;
}

/* Model viewer styles */
model-viewer {
  width: 100%;
  height: 100%;
  background-color: transparent;
  --progress-bar-color: #4F46E5;
  --progress-bar-height: 4px;
}

model-viewer::part(default-progress-bar) {
  background-color: var(--progress-bar-color);
  height: var(--progress-bar-height);
  border-radius: 2px;
}

model-viewer::part(default-progress-mask) {
  background: rgba(0, 0, 0, 0.1);
  border-radius: 2px;
}

/* Loading spinner */
.loading-spinner {
  border: 2px solid var(--muted);
  border-top: 2px solid var(--primary);
  border-radius: 50%;
  width: 20px;
  height: 20px;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: var(--background);
}

::-webkit-scrollbar-thumb {
  background: var(--muted);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--muted-foreground);
}

