  const [description, setDescription] = useState(currentDescription);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const updateMutation = useMutation({
    mutationFn: async (data: { title: string; description: string; password: string }) => {
      const response = await fetch(`/api/content/${bucket}/${encodeURIComponent(fileName)}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'C·∫≠p nh·∫≠t th·∫•t b·∫°i');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "C·∫≠p nh·∫≠t th√†nh c√¥ng!",
        description: `ƒê√£ c·∫≠p nh·∫≠t th√¥ng tin cho "${fileName}"`,
      });

      queryClient.invalidateQueries({ queryKey: ['/api/content', bucket] });
      setEditDialogOpen(false);
      onUpdate?.();
    },
    onError: (error: Error) => {
      toast({
        title: "L·ªói c·∫≠p nh·∫≠t",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (password: string) => {
      const response = await fetch(`/api/content/${bucket}/${encodeURIComponent(fileName)}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ password }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'X√≥a th·∫•t b·∫°i');
      }

      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "X√≥a th√†nh c√¥ng!",
        description: `ƒê√£ x√≥a "${fileName}"`,
      });

      queryClient.invalidateQueries({ queryKey: ['/api/content', bucket] });
      setDeleteDialogOpen(false);
      onUpdate?.();
    },
    onError: (error: Error) => {
      toast({
        title: "L·ªói x√≥a file",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleEdit = () => {
    const password = prompt('Nh·∫≠p m·∫≠t kh·∫©u ƒë·ªÉ ch·ªânh s·ª≠a:');
    if (!password) {
      toast({
        title: "L·ªói",
        description: "C·∫ßn m·∫≠t kh·∫©u ƒë·ªÉ ch·ªânh s·ª≠a!",
        variant: "destructive",
      });
      return;
    }

    updateMutation.mutate({ title, description, password });
  };

  const handleDelete = () => {
    const password = prompt('Nh·∫≠p m·∫≠t kh·∫©u ƒë·ªÉ x√≥a:');
    if (!password) {
      toast({
        title: "L·ªói",
        description: "C·∫ßn m·∫≠t kh·∫©u ƒë·ªÉ x√≥a!",
        variant: "destructive",
      });
      return;
    }

    deleteMutation.mutate(password);
  };

  return (
    <>
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button
            variant="ghost"
            size="sm"
            className="absolute top-2 right-2 h-8 w-8 p-0 bg-black/50 text-white hover:bg-black/70"
            data-testid="content-actions-menu"
          >
            <MoreVertical className="h-4 w-4" />
