import { useState } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { Upload as UploadIcon, File, Image, Video, Box } from 'lucide-react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Progress } from '@/components/ui/progress';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

type ContentType = 'image' | 'video' | 'model';

interface UploadResponse {
  ok: boolean;
  kind: string;
  url: string;
  path: string;
  message?: string;
}

export default function Upload() {
  const [contentType, setContentType] = useState<ContentType>('image');
  const [file, setFile] = useState<File | null>(null);
  const [title, setTitle] = useState('');
  const [uploadProgress, setUploadProgress] = useState(0);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const uploadMutation = useMutation({
    mutationFn: async (data: { file: File; contentType: ContentType; title: string; password: string }) => {
      const formData = new FormData();
      formData.append('file', data.file);
      formData.append('kind', data.contentType);
      formData.append('title', data.title);
      formData.append('password', data.password);

      const response = await fetch('/api/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Upload failed');
      }

      return response.json() as Promise<UploadResponse>;
    },
    onSuccess: (data) => {
      toast({
        title: "Upload th√†nh c√¥ng!",
        description: `File ƒë√£ ƒë∆∞·ª£c t·∫£i l√™n: ${file?.name}`,
      });

      // Invalidate and refetch content
      const bucketName = contentType === 'image' ? 'images' : contentType === 'video' ? 'videos' : 'models';
      queryClient.invalidateQueries({ queryKey: ['/api/content', bucketName] });

      // Reset form
      setFile(null);
      setTitle('');
      setUploadProgress(0);

      // Show public URL
      setTimeout(() => {
        toast({
          title: "URL c√¥ng khai",
          description: data.url,
          duration: 8000,
        });
      }, 1500);
    },
    onError: (error: Error) => {
      toast({
        title: "L·ªói upload",
        description: error.message,
        variant: "destructive",
      });
      setUploadProgress(0);
    },
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!file) {
      toast({
        title: "L·ªói",
        description: "Vui l√≤ng ch·ªçn file ƒë·ªÉ t·∫£i l√™n!",
        variant: "destructive",
      });
      return;
    }

    // File size validation (200MB)
    if (file.size > 200 * 1024 * 1024) {
      toast({
        title: "L·ªói",
        description: "File qu√° l·ªõn! Gi·ªõi h·∫°n 200MB.",
        variant: "destructive",
      });
      return;
    }

    // File type validation
    const validTypes: Record<ContentType, string[]> = {
      image: ['image/png', 'image/jpeg', 'image/jpg'],
      video: ['video/mp4', 'video/webm'],
      model: ['model/gltf+json', 'model/gltf-binary', 'application/octet-stream'],
    };

    const isValidType = contentType === 'model' 
      ? validTypes[contentType].includes(file.type) || 
        file.name.toLowerCase().endsWith('.glb') || 
        file.name.toLowerCase().endsWith('.gltf')
      : validTypes[contentType].includes(file.type);

    if (!isValidType) {
      toast({
        title: "L·ªói",
        description: "Lo·∫°i file kh√¥ng h·ª£p l·ªá!",
        variant: "destructive",
      });
      return;
    }

    // Password prompt
    const password = prompt('Nh·∫≠p m·∫≠t kh·∫©u upload:');
    if (!password) {
      toast({
        title: "L·ªói",
        description: "C·∫ßn m·∫≠t kh·∫©u ƒë·ªÉ t·∫£i l√™n!",
        variant: "destructive",
      });
      return;
    }

    // Simulate upload progress
    setUploadProgress(0);
    const progressInterval = setInterval(() => {
      setUploadProgress(prev => {
        const newProgress = prev + 10;
        if (newProgress >= 90) {
          clearInterval(progressInterval);
          return 90;
        }
        return newProgress;
      });
    }, 200);

    try {
      await uploadMutation.mutateAsync({ file, contentType, title, password });
      setUploadProgress(100);
    } catch {
      clearInterval(progressInterval);
      setUploadProgress(0);
    }
  };

  const getAcceptTypes = (type: ContentType) => {
    switch (type) {
      case 'image': return '.png,.jpg,.jpeg';
      case 'video': return '.mp4,.webm';
      case 'model': return '.glb,.gltf';
      default: return '';
    }
  };

  const getIcon = (type: ContentType) => {
    switch (type) {
      case 'image': return <Image className="w-5 h-5" />;
      case 'video': return <Video className="w-5 h-5" />;
      case 'model': return <Box className="w-5 h-5" />;
      default: return <File className="w-5 h-5" />;
    }
  };

  return (
    <div className="min-h-screen bg-card py-20">
      <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-12">
          <h2 className="font-display text-3xl md:text-4xl font-bold mb-4">T·∫£i L√™n N·ªôi Dung</h2>
          <p className="text-xl text-muted-foreground">Chia s·∫ª m√¥ h√¨nh 3D, h√¨nh ·∫£nh v√† video m·ªõi</p>
        </div>

        <Card>
          <CardContent className="p-8">
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <Label htmlFor="content-type" className="text-sm font-medium mb-2 block">
                  Lo·∫°i N·ªôi Dung
                </Label>
                <Select value={contentType} onValueChange={(value: ContentType) => setContentType(value)}>
                  <SelectTrigger data-testid="select-content-type">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="image">
                      <div className="flex items-center gap-2">
                        <Image className="w-4 h-4" />
                        H√¨nh ·∫¢nh (PNG, JPG)
                      </div>
                    </SelectItem>
                    <SelectItem value="video">
                      <div className="flex items-center gap-2">
                        <Video className="w-4 h-4" />
                        Video (MP4, WebM)
                      </div>
                    </SelectItem>
                    <SelectItem value="model">
